script:
frappe.ui.form.on('User', {
    refresh: function(frm) {
        if (!cur_frm.doc.__islocal) {
            frm.add_custom_button(__('Collect Face Model'), function() {
                // Create a new dialog
                let dialog = new frappe.ui.Dialog({
                    title: __('Instructions'),
                    indicator: 'green',
                    fields: [
                        {
                            fieldtype: 'HTML',
                            fieldname: 'instructions_html',
                            options: `
                                <div>
                                    <img src="/assets/face_recognition_login/img/instructions.jpg" alt="Instructions Image" style="width: 100%; height: auto;">
                                    <p>After clicking OK, please do the following:</p>
                                    <ol>
                                        <li>Move your head at different angles for better accuracy.</li>
                                        <li>Stare at the camera until we collect the required models for your face.</li>
                                        <li>A success message will pop up when finished.</li>
                                    </ol>
                                    <p style="color: red; font-weight: bold;">Important: Make sure you follow all the steps carefully to ensure the accuracy of the model.</p>
                                </div>
                            `
                        },
                        {
                            fieldtype: 'Check',
                            fieldname: 'accept_instructions',
                            label: __('I have read and understood the instructions'),
                            reqd: 1
                        }
                    ],
                    primary_action_label: __('OK'),
                    primary_action(values) {
                        if (values.accept_instructions) {
                            frappe.msgprint(__('Hi'));
                            dialog.hide();
                        } else {
                            frappe.msgprint(__('Please accept the instructions to proceed.'));
                        }
                    }
                });

                // Show the dialog
                dialog.show();
            }).addClass('btn-success');
        }
    }
});
